mapper.insert:
INSERT INTO book_editor( book_id,editor_id )

    SELECT #{item.bookId},#{item.editorId} 
      FROM DUAL

mapper.delete:
DELETE FROM book_editor
WHERE

    editor_id = #{item.editorId} AND book_id = #{item.bookId}

mapper.select:
SELECT book_id, editor_id
 FROM book_editor
WHERE editor_id = #{editorId} AND book_id = #{bookId}

SELECT book_id, editor_id
 FROM book_editor


       AND book_id = #{bookId}


       AND editor_id = #{editorId}
